mapper.select:
select

from ecbu_store


        AND start_type=#{startType}


        AND ec_company_id=#{ecCompanyId}


order by sort_id asc

select count(*) from ecbu_store


        AND start_type=#{startType}


        AND ec_company_id=#{ecCompanyId}

select

from ecbu_store


        AND ec_company_id=#{ecCompanyId}


        AND sort_id > #{sortId,jdbcType=INTEGER}

select

from ecbu_store


        and ecbus_id = #{ecbusId,jdbcType=INTEGER}


        and ec_company_id = #{ecCompanyId,jdbcType=INTEGER}


        and store_name = #{storeName,jdbcType=VARCHAR}


        and default_type = #{defaultType,jdbcType=BIT}

select

from ecbu_store


        and ecbus_id != #{ecbusId,jdbcType=INTEGER}


        and ec_company_id = #{ecCompanyId,jdbcType=INTEGER}


        and store_name = #{storeName,jdbcType=VARCHAR}

mapper.delete:
delete
from ecbu_store


        and ecbus_id = #{ecbusId,jdbcType=INTEGER}


        and ec_company_id = #{ecCompanyId,jdbcType=INTEGER}

mapper.update:
update ecbu_store

    default_type = false



        and ec_company_id = #{ecCompanyId,jdbcType=INTEGER}
