mapper.select:
select
*
from ecu_offer as ecuo


        and ecuo.start_type=#{startType}


        and ecuo.ecqul_id=#{ecqulId}


        and ecuo.sort_id > #{sortId,jdbcType=INTEGER}


order by ecuo.sort_id asc

select count(*) from ecu_offer


        and start_type=#{startType}


        and ecqul_id=#{ecqulId}

select

from ecu_offer as ecuo
left join ecqu_level as ecqul on ecuo.ecqul_id=ecqul.ecqul_id


        and ecuo.ecuo_id = #{ecuoId,jdbcType=INTEGER}


        and ecuo.ec_company_id = #{ecCompanyId,jdbcType=INTEGER}


        and ecuo.ecqul_id = #{ecqulId,jdbcType=INTEGER}


        and ecuo.area_str = #{areaStr,jdbcType=INTEGER}


        and ecuo.ecuo_id != #{ecuoId,jdbcType=INTEGER}


        and ecuo.area_str = #{areaStr,jdbcType=VARCHAR}


order by ecuo.sort_id desc limit 1

mapper.delete:
delete
from ecu_offer


        and ecuo_id = #{ecuoId,jdbcType=INTEGER}


        and ec_company_id = #{ecCompanyId,jdbcType=INTEGER}

mapper.update:
update ecu_offer
set sort_id = sort_id - 1
where ecqul_id = #{ecqulId}
  and sort_id > #{sortId}
